import React, { useState } from "react";
import jwt from "jsonwebtoken";
import { useNavigate } from "react-router-dom";

interface User {
  id: number;
  username: string;
  password: string;
}

const USERS: User[] = [
  { id: 1, username: "SethMyers", password: "test-password" },
];

const SECRET_KEY: string = process.env.REACT_APP_API_TOKEN as string;

const LoginPage: React.FC = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    const isAuthenticated = USERS.some(
      (u) => u.username === username && u.password === password
    );

    if (isAuthenticated) {
      if (SECRET_KEY == "key-missing") {
        throw new Error("API Key is missing");
      }

      const token = jwt.sign({ id: 1, username }, SECRET_KEY, {
        expiresIn: "1h",
      });
      localStorage.setItem("token", token);
      navigate("/weather-app");
    } else {
      alert("Invalid credentials");
    }
  };

  return (
    <div>
      <header>
        <h1>Weather Login</h1>
      </header>
      <div>
        <p>Username:</p>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <p>Password:</p>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>
      <button onClick={handleLogin}>Log in</button>
    </div>
  );
};

export default LoginPage;
