import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

interface City {
  id: number;
  name: string;
  country: string;
}

const CITIES: City[] = [
  { id:  6167865, name: 'Toronto', country: 'CA' },
  { id:  6094817, name: 'Ottawa', country: 'CA' },
  { id:  1850147, name: 'Tokyo', country: 'JP' }
];

const App: React.FC = () => {
  const [selectedCity, setSelectedCity] = useState<City | null>(null);
  const [weatherData, setWeatherData] = useState<any>(null);

  useEffect(() => {
    if (selectedCity) {
      axios.get(`/weather/${selectedCity.id}`)
        .then(response => setWeatherData(response.data))
        .catch(error => console.error('Error fetching weather data'));
    }
  }, [selectedCity]);

  const handleCitySelection = (event: React.ChangeEvent<HTMLSelectElement>) => {
    const city = CITIES.find(c => c.name === event.target.value) || null;
    setSelectedCity(city);
  };

  const kelvinToCelsius = (kelvin: number): number => Math.round(kelvin -  273.15);

  return (
    <div>
      <header>
        <h1>Weather Forecast</h1>
      </header>
      {selectedCity && <div className="cityLabel">City</div>}
      <select value={selectedCity?.name || ''} onChange={handleCitySelection}>
        <option value="" disabled>Select a city</option>
        {CITIES.map(city => (
          <option key={city.id} value={city.name}>{city.name}</option>
        ))}
      </select>
      {weatherData && (
        <div>
          <h2>{weatherData.weather[0].main}</h2>
          <p>{weatherData.weather[0].description}</p>
          <p>{kelvinToCelsius(weatherData.main.temp)}Â°C</p>
          <p>Wind {Math.round(weatherData.wind.speed)} m/s</p>
        </div>
      )}
      {/* Render forecast list */}
    </div>
  );
};

export default App;
