import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

interface City {
  id: number;
  name: string;
  country: string;
}

const CITIES: City[] = [
  { id:  6167865, name: 'Toronto', country: 'CA' },
  { id:  6094817, name: 'Ottawa', country: 'CA' },
  { id:  1850147, name: 'Tokyo', country: 'JP' }
];

const App: React.FC = () => {
  const [selectedCity, setSelectedCity] = useState<City | null>(null);
  const [weatherData, setWeatherData] = useState<any>(null);
  const [forecastData, setForecastData] = useState<any[]>([]);
  const [showForecast, setShowForecast] = useState(false);
  const [selectedDate, setSelectedDate] = useState<string | null>(null);
  const [uniqueDates, setUniqueDates] = useState<string[]>([]);

  // Set city weather data if a city is selected
  useEffect(() => {
    if (selectedCity) {
      axios.get(`/weather/${selectedCity.id}`)
        .then(response => setWeatherData(response.data))
        .catch(error => console.error('Error fetching weather data'));
    }
  }, [selectedCity]);

  // Set selected city when city is selected
  const handleCitySelection = (event: React.ChangeEvent<HTMLSelectElement>) => {
    const city = CITIES.find(c => c.name === event.target.value) || null;
    setSelectedCity(city);
  };

  // Set detailed forecast data if show forecast is clicked and a city is selected, and hide it if its already selected
  const handleForecastClick = () => {
    if (!showForecast && selectedCity != null) {
      axios.get(`/forecast/${selectedCity.id}`)
        .then(response => setForecastData(response.data.list))
        .catch(error => console.error('Error fetching forecast data'));
    }
    setShowForecast(!showForecast);
  };

  // Set dates if forecast data exists
  useEffect(() => {
    if (forecastData.length >  0) {
      const dates = Array.from(new Set(forecastData.map(item => item.dt_txt)));
      setUniqueDates(dates);
      setSelectedDate(dates[0]); // Auto-select the earliest date
    }
  }, [forecastData]);

  // Set current date selection if a different date is clicked
  const handleDateClick = (date: string) => {
    setSelectedDate(date);
  };

  // Forecast data for current date
  const filteredForecastData = forecastData.filter(item => item.dt_txt === selectedDate);

  const kelvinToCelsius = (kelvin: number): number => Math.round(kelvin -  273.15);

  return (
    <div>
      <header>
        <h1>Weather Forecast</h1>
      </header>
      {selectedCity && <div className="cityLabel">City</div>}
      <select value={selectedCity?.name || ''} onChange={handleCitySelection}>
        <option value="" disabled>City</option>
        {CITIES.map(city => (
          <option key={city.id} value={city.name}>{city.name}</option>
        ))}
      </select>
      {weatherData && (
        <div>
          <h2>{weatherData.weather[0].main}</h2>
          <p>{weatherData.weather[0].description}</p>
          <p>{kelvinToCelsius(weatherData.main.temp)}째C</p>
          <p>Wind {Math.round(weatherData.wind.speed)} m/s</p>
        </div>
      )}
      {selectedCity && (
        <>
          {/* Render the "See Forecast" button */}
          <button onClick={handleForecastClick}>
            {showForecast ? 'Close' : 'See Forecast'}
          </button>
          {/* Render the forecast table if showForecast is true */}
          {showForecast && (
            <table className='forecast-table'>
              <thead>
                <tr>
                  <th>Date</th>
                  <th>Temp</th>
                  <th>Min Temp</th>
                  <th>Max Temp</th>
                  <th>Wind</th>
                  <th>Description</th>
                </tr>
              </thead>
              <tbody>
                {filteredForecastData.map((forecast, index) => (
                  <tr key={index}>
                    <td>{forecast.dt_txt}</td>
                    <td>{kelvinToCelsius(forecast.main.temp)}째C</td>
                    <td>{kelvinToCelsius(forecast.main.temp_min)}째C</td>
                    <td>{kelvinToCelsius(forecast.main.temp_max)}째C</td>
                    <td>{Math.round(forecast.wind.speed)} m/s</td>
                    <td>{forecast.weather[0].description}</td>
                  </tr>
                ))}
              </tbody>
            </table>

          )}
          {/* Render buttons for day selection */}
          {uniqueDates.map((date, index) => (
                <button key={index} onClick={() => handleDateClick(date)}>
                  {date}
                </button>
          ))}
        </>
      )}
    </div>
  );
}

export default App;
